import React, { useState } from "react";

function App() {
  const [authStatus, setAuthStatus] = useState(false);
  const [emailDetails, setEmailDetails] = useState({
    from: "",
    to: "",
    subject: "",
    message: "",
  });

  const handleLogin = () => {
    window.location.href = "http://localhost:3001/login";
  };

  const sendEmail = async () => {
    try {
      const response = await fetch("http://localhost:3001/send-email", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(emailDetails),
      });
      const data = await response.json();
      alert(data.message || data.error);
    } catch (error) {
      console.error("Error sending email:", error);
    }
  };

  return (
    <div className="App">
      {!authStatus ? (
        <button onClick={handleLogin}>Login with Gmail</button>
      ) : (
        <div>
          <h1>Send an Email</h1>
          <input
            type="text"
            placeholder="From"
            value={emailDetails.from}
            onChange={(e) => setEmailDetails({ ...emailDetails, from: e.target.value })}
          />
          <input
            type="text"
            placeholder="To"
            value={emailDetails.to}
            onChange={(e) => setEmailDetails({ ...emailDetails, to: e.target.value })}
          />
          <input
            type="text"
            placeholder="Subject"
            value={emailDetails.subject}
            onChange={(e) => setEmailDetails({ ...emailDetails, subject: e.target.value })}
          />
          <textarea
            placeholder="Message"
            value={emailDetails.message}
            onChange={(e) => setEmailDetails({ ...emailDetails, message: e.target.value })}
          ></textarea>
          <button onClick={sendEmail}>Send Email</button>
        </div>
      )}
    </div>
  );
}

export default App;
