import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Check if the user is logged in by checking credentials stored in session
    axios.get('http://localhost:3001/')
      .then(response => {
        setUser(response.data.message); // This would contain user info if authenticated
      })
      .catch(error => {
        setUser(null); // If error, reset user state
      });
  }, []);

  const handleLogin = () => {
    window.location.href = 'http://localhost:3001/login';  // Redirect to backend login route
  };

  return (
    <div>
      <h1>Welcome to the Google OAuth App</h1>
      {user ? (
        <div>
          <h2>{user}</h2>
        </div>
      ) : (
        <div>
          <button onClick={handleLogin}>Login with Google</button>
        </div>
      )}
    </div>
  );
}

export default App;
